import Layout from "../components/Layout";
import BasicMeta from "../components/meta/BasicMeta";
import OpenGraphMeta from "../components/meta/OpenGraphMeta";
import TwitterCardMeta from "../components/meta/TwitterCardMeta";
import { SocialList } from "../components/SocialList";

export default function Index() {
  return (
    <Layout>
      <BasicMeta url={"/"} />
      <OpenGraphMeta url={"/"} />
      <TwitterCardMeta url={"/"} />
      <div className="container">
        <div>
          <h1>
            Sobre<span className="fancy">!</span>
          </h1>
          <span className="handle"></span>
          <h2></h2>
          <p className="sobre">Sou apaixonado por tecnologia e a forma como ela pode transformar a sociedade. 
            <br/>
            Atualmente me dedico a aprender programação, mais recentemente flutter e linux ♥. </p>

           <p className="sobre"> O pesquisador Edgar Dale dizia que depois de duas semanas, o cérebro 
             humano lembra 
             <br/>
             10% do que leu; 20% do que ouviu; 30% do que viu; 50% do 
             que viu e ouviu; 70% do que 
             <br/> disse em uma conversa/debate; e 90% do que 
             vivenciou a partir de sua prática. O estudo ficou conhecido pelo nome <a href="https://miro.medium.com/max/3752/1*rewFR_9JPe9RoCHFKdezTg.png">The 
             cone of learning.</a></p>

          <p className="sobre">Criei esta página com o objetivo de solidificar e compartilhar um pouco 
            dos meus estudos.</p>
          <SocialList />
        </div>
      </div>
      <style jsx>{`
        * {
          border: 1px solid blue; /* linhas guia */
        }
        .container {
          display: flex;
          align-items: center;
          justify-content: center;
          flex: 1 1 auto;
          padding: 0 1.5rem;
          max-width: 100%;
        }
        h1 {
          font-size: 2.5rem;
          margin: 0;
          font-weight: 500;
        }
        h2 {
          font-size: 1.75rem;
          font-weight: 400;
          line-height: 1.25;
        }
        .sobre {
          text-align: justify;
        }
        .fancy {
          color: #15847d;
        }
        .handle {
          display: inline-block;
          margin-top: 0.275em;
          color: #9b9b9b;
          letter-spacing: 0.05em;
        }

        @media (min-width: 769px) {
          h1 {
            font-size: 3rem;
          }
          h2 {
            font-size: 2.25rem;
          }
        }
      `}</style>
    </Layout>
  );
}
