import Layout from "../components/Layout";
import BasicMeta from "../components/meta/BasicMeta";
import OpenGraphMeta from "../components/meta/OpenGraphMeta";
import TwitterCardMeta from "../components/meta/TwitterCardMeta";
import { SocialList } from "../components/SocialList";

export default function Index() {
  return (
    <Layout>
      <BasicMeta url={"/"} />
      <OpenGraphMeta url={"/"} />
      <TwitterCardMeta url={"/"} />
      <div className="container">
        <div>
          <h1>
            Sobre<span className="fancy">!</span>
          </h1>
          <span className="handle"></span>
          <h2></h2>
          <p className="sobre">Sou apaixonado por tecnologia e a forma como ela pode transformar a sociedade. 
            Tenho um background em administração com especialização em finanças, contudo, 
            atualmente me dedico a aprender programação, mais recentemente flutter e linux ♥. </p>


          <p className="sobre">Criei esta página com o objetivo de solidificar e compartilhar um pouco 
            dos meus estudos.</p>
          <SocialList />
        </div>
      </div>
      <style jsx>{`
        * {
          border: 1px solid blue; /* linhas guia */
        }
        .container {
          display: flex;
          align-items: center;
          justify-content: center;
          flex: 1 1 auto;
          padding: 0 1.5rem;
        }
        h1 {
          font-size: 2.5rem;
          margin: 0;
          font-weight: 500;
        }
        h2 {
          font-size: 1.75rem;
          font-weight: 400;
          line-height: 1.25;
        }
        .sobre {
          width: 50%;
          text-align: justify;
        }
        .fancy {
          color: #15847d;
        }
        .handle {
          display: inline-block;
          margin-top: 0.275em;
          color: #9b9b9b;
          letter-spacing: 0.05em;
        }

        @media (min-width: 769px) {
          h1 {
            font-size: 3rem;
          }
          h2 {
            font-size: 2.25rem;
          }
        }
      `}</style>
    </Layout>
  );
}
